mapper.insert:
 		INSERT INTO TB_PUSH_MSG(
 			seq,
	 		title, 
	 		message, 
	 		reserve_time, 
	 		atch_file_id, 
	 		push_flag,
	 		contents_flag,
	 		push_url,
	 		reg_date
 		)VALUES(
 			nextVal('TB_PUSH_MSG')
 			,#{title} 
 			,#{message} 
	 		,#{reserveTime} 
	 		,#{atchFileId} 
	 		,#{pushFlag}
	 		,#{contentsFlag}
	 		,#{pushUrl}
 			,now()
 		)

mapper.update:
  		UPDATE CO_Notice SET
 			title = #{title}
 			,contents = #{contents}
			,NOTICE_YN = #{topYn}
			,atch_file_id = #{atchFileId}
			,LASTUPDATE_USER = #{adminName}
			,LASTUPDATE_DATE = GETDATE()
 		WHERE SEQ = #{seq};

  		UPDATE CO_InquiryAnswer SET
 			contents = #{contents}
			,LASTUPDATE_USER = #{answerName}
			,LASTUPDATE_DATE = GETDATE()
 		WHERE SEQ = #{seq};

 		UPDATE CO_Inquiry SET
			INQ_YN = 'Y'
		WHERE SEQ = #{inqId};

  		UPDATE CO_FAQNotice SET
 			INQUIRY_CONTENTS = #{inquiryContent}
 			,ANSWER_CONTENTS = #{ansewrContent}
			,LASTUPDATE_USER = #{adminName}
			,LASTUPDATE_DATE = GETDATE()
 		WHERE SEQ = #{seq};

UPDATE TB_PUSH_MSG SET
	message = #{message}
	,title = #{title}
	,reserve_time = #{reserveTime}
	,atch_file_id = #{atchFileId}
	,push_flag = #{pushFlag}
	,contents_flag = #{contentsFlag}
	,push_url = #{pushUrl}
WHERE SEQ = #{seq}
